def find_basement_position(directions):
    floor = 0
    for i in range(len(directions)):
        if directions[i] == '(':
            floor += 1
        else:
            floor -= 1
        if floor < 0:
            return i + 1


if __name__ == '__main__':
    print(find_basement_position(INPUT))
